/* Hide annoying stuff */
.give-gold-button,
.goldvertisement,
.embed-comment,
.nub,
.sr-list > .separator,
.sr-list > .separator + .sr-bar,
#sr-more-link {
	display: none !important;
}
/* Reset */
* {
	background-color: transparent !important;
	border: none !important;
	color: #777 !important;
}
/* Default links */
a {
	color: #48b !important;
}
a:visited {
	color: #369 !important;
}
a:hover {
	color: #5ad !important;
}
/* Background */
html,
body {
	background-color: #222 !important;
}
/* Top bar */
#header-img {
	filter: invert(80%) hue-rotate(180deg) !important;
}
#sr-header-area {
	height: 0 !important;
}
.sr-bar,
.dropdown.srdrop {
	float: right !important;
	margin-left: 1em;
}
.drop-choices,
.hover-bubble {
	background: #111 !important;
}
.tabmenu .selected a {
	color: #999 !important;
}
/* Side bar */
.listing-chooser-collapsed .grippy {
	width: 1px !important;
}
.listing-chooser-collapsed .listing-chooser {
	padding-right: 1px !important;
}
.listing-chooser li.selected {
	box-shadow: none !important;
}
.grippy:after {
	display: none !important;
}
/* Inputs and buttons */
input,
textarea,
.linkinfo .shortlink input,
.new-comment .usertext-body,
.morelink a,
.morelink:hover a,
.fancy-toggle-button a,
.usertext button {
	background: #333 !important;
	color: #999 !important;
	font-weight: normal !important;
}
/* Listing */
.thing .title {
	color: #aaa !important;
}
.title:visited {
	color: #777 !important;
}
.thing .title:hover {
	color: #ccc !important;
}
.arrow {
	opacity: 0.3 !important;
}
.expando-button {
	opacity: 0.3 !important;
	background-color: transparent !important;
}
.moderator,
.green {
	color: #3a3 !important;
}
.admin,
.nsfw-stamp * {
	color: #a66 !important;
}
.pagename a,
.trophy-name {
	color: #999 !important;
}
.buttons li {
	padding: 0 !important;
}
.buttons a {
	margin-right: 8px !important;;
	color: #888 !important;
}
.buttons a:visited {
	color: #666 !important;
}
.buttons a:hover {
	color: #aaa !important;
}
.pagename,
.tabmenu li,
.link .midcol,
.buttons a,
.subreddit {
	font-weight: normal !important;
}
.search-expando.collapsed:before,
.comment-fade {
	display: none !important;
}
/* Comments */
.link .usertext .md,
blockquote,
pre,
code,
.gold-accent {
	background-color: #111 !important;
}
.md blockquote {
	border-left: solid 4px #333 !important;
}
.md td {
	border: solid 1px #333 !important;
}
hr {
	border-bottom: solid 1px #333 !important;
}
.comment .author,
.morecomments a {
	font-weight: normal !important;
}
/* RES */
.guider,
.guiders_button,
.RESDialogSmall,
.RESDropdownOptions,
.res-fancy-toggle-button,
#RESConsoleContainer,
#RESShortcutsAddFormContainer {
	background: #222 !important;
}

@font-face {
