@namespace url(http://www.w3.org/1999/xhtml);

/*** Variables ***/
    :root {
        --added-width: 200px;
    }
    
    body {
        background-color: #111;
    }
    
    div.body {
        background-color: #161616;
        /*     background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat; */
        color: #aaa;
    }
    
    div.document {
        background: #012028;
    }
    
    div.body h1, div.body h2, div.body h3, div.body h4, div.body h5, div.body h6 {
        background: none;
        color: #3776A3;
        border-bottom-color: #444;
    }
    
    div.related, div.sphinxsidebar {
        background-color: #111;
    }
    
    div.related {
        border-top-color: #333;
    }
    
    /** Left Pane - Bottom Layer **/
    div.sphinxsidebar {
        width: calc(var(--added-width) + 230px) !important;
    }
    
    div.sphinxsidebar[style="width: 230px;"] {
        width: calc(var(--added-width) + 230px) !important;
    }
    
    div.sphinxsidebar[style="width: 0.8em;"] {
        width: 0.8em !important;
    }
    
    /* Left-side item bullets */
    div.sphinxsidebar ul {
        color: #333;
    }
    
     /* Sub-headings */
    div.sphinxsidebar h4, div.sphinxsidebar h3, div.sphinxsidebar p {
        color: #aaa;
    }
    
    /* Search */
    div.sphinxsidebar input {
        border: 1px solid #333;
    }
    
    form.inline-search input {
        border: 1px solid #333;
    }
    
    /** Search results **/
/*     ul.search li {
        filter: invert(80%);
    } */
    
    /** Left Pane - Top Layer **/
    div.sphinxsidebarwrapper {
        background-color: #111;
        width: calc(var(--added-width) + 200px) !important;
    }
    
    /** Right-side Page Contents **/
    div.documentwrapper {
        background-color: #161616;
    }
    
    div.bodywrapper {
        margin-left: calc(var(--added-width) + 230px);
    }
    
    div.bodywrapper[style="margin-left: 230px;"] {
        margin-left: calc(var(--added-width) + 230px) !important;
    }
    
    div.bodywrapper[style="margin-left: 0.8em;"] {
        margin-left: 0.8em !important;
    }
    
    /* Sidebar symbols - «, » */
    div#sidebarbutton {
        color: #666 !important;
    }
    
    
    div.note {
        background-color: #111;
        border-color: #000;
    }
    
    div.related:first-child {
        border-bottom-color: #333;
    }
    
    div#sidebarbutton {
        background-color: #000 !important;
    }
    
    a {
        color: #4983AB !important;
    }
    
    tt {
        background: none;
    }
    
    pre {
        background: #222;
        color: #AAA;
        font-size: 150%;
        border-color: #333 !important;
    }
    
    
    /* Code Copy button */
    .copybutton {
        color: #666 !important;
        border-color: #333 !important;
    }
    
    .highlight .go { /*output*/ 
        color: #AAA;
    }
    
    .highlight .gt, .highlight .nf { /*tracebacks*/
        color: #4772D1;
    }
    
    .highlight .nd {
        color: #aaa;
    }
    
    table.docutils thead tr {
        background-color: #333;
    }
    
    table.docutils tbody tr.row-even {
        background-color: #222;
    }
    
    table.docutils tbody tr.row-odd {
        background-color: #181818;
    }
    
    table.docutils td, table.docutils th {
        background-color: inherit;
        border-color: #333 !important;
    }
    
    select, input {
        background-color: #111;
        color: #AAA;
    }
    
    input[name="q"] {
