	color: #FFFFFF;
	padding: 0 1em;
	font-family: "Open Sans", sans-serif;
}
.playground {
	display: flex;
	height: 100vh;
	flex-direction: column;
	padding-bottom: 1em;
}
.playground-split {
	display: flex;
	height: 100vh;
}
@media screen and (max-width: 1600px) {
	.playground-split-automatic {
		flex-direction: column;
	}
}
@media screen and (min-width: 1600px) {
	.playground-split-automatic {
		flex-direction: row;
	}
}
.playground-split-horizontal {
	flex-direction: column;
}
.playground-split-vertical {
	flex-direction: row;
}
.playground-split-vertical > *:first-child {
	margin-top: 0;
	margin-bottom: 0;
	margin-left: 0;
}
@media screen and (min-width: 1600px) {
	.playground-split-automatic > *:first-child {
		margin-top: 0;
		margin-bottom: 0;
		margin-left: 0;
	}
}
.playground-split-vertical > * {
	margin-left: 0.5em;
	margin-top: -0.2em;
	margin-bottom: 4px;
}
@media screen and (min-width: 1600px) {
	.playground-split-automatic > * {
		margin-left: 0.5em;
		margin-top: -0.2em;
		margin-bottom: 4px;
	}
}
.playground-editor {
	flex: 200 1 auto;
	position: relative;
	border: 0px solid #BBBBBB;
	border-radius: 0;
}
.playground-output-focused {
	flex: 110 1 auto;
	position: relative;
}
.configuration {
	position: relative;
	width: 100%;
	height: 100%;
}
.configuration-item {
	display: flex;
	margin-bottom: 0.5em;
}
.configuration-label {
	flex: 1 1 100%;
}
.configuration-value {
	flex: 1 1 100%;
}
.configuration-actions {
	right: 0;
	bottom: 0;
	position: absolute;
}
#editor {
	position: absolute;
	font-size: 1.4em;
	background-color: #000000;
	color: #FFFFFF;
	font-family: 'Fira Code', 'Source Code Pro', monospace;
}
.editor-simple {
	position: absolute;
	width: 100%;
	height: 100%;
	border: none;
	font-size: 1.4em;
	font-family: 'Fira Code', 'Source Code Pro', monospace;
	background-color: #000000;
	color: #FFFFFF;
	padding: 1em;
}
.output {
	display: flex;
	flex-direction: column;
	margin-top: 0.2em;
}
.playground-output-focused .output {
	position: absolute;
	width: 100%;
	height: 100%;
}
.output-tabs {
	display: flex;
}
.output-tab {
	flex: 1 1 auto;
	line-height: 1.5em;
	background-color: #0e0e0e;
	color: #fff;
	border: 0px solid #fff;
	border-right: none;
	cursor: pointer;
}
.output-tab:last-of-type {
	border-right: 0px solid #BBBBBB;
}
.output-tab-selected {
	background-color: #0e0e0e;
	color: #fff;
	border-bottom: none;
	cursor: default;
