mark, .reviews, .draft, .privilegedBadge, .renamed, .secureContexts, .todo, .translationInProgress, .warning.warning-review
{ background-color: var(--bg-warning); color: var(--fg-warning) }
.reviews a, .draft a, .privilegedBadge a, .renamed a, .secureContexts a, .todo a, .translationInProgress a, .warning.warning-review a { color: var(--fg-warning); text-decoration: underline }

/* Compatibility table */
*[style*="background-color: rgb(204, 255, 153)"], span[style*="background: #309c40"] { background-color: #3a3 !important }
*[style*="background-color: rgb(255, 204, 204)"] { background-color: #a33 !important }
*[style*="background-color: rgb(255, 255, 153)"] { background-color: #993 !important }


/* Borders */
.callout-box, #main-header, ul.tags li, .text-content table { border: none }
.communitycontact a { border-color: var(--fg-link) }
.communitycontact a:hover, .communitycontact a:focus { border-color: var(--fg-link-hover) }
.text-content article[id="wikiArticle"] > div:not([class]) > h2::before, .text-content article[id="wikiArticle"] > h2::before, .text-content table.standard-table:not(.learn-box), .text-content table, .text-content table.standard-table:not(.learn-box) th, .text-content table.standard-table:not(.learn-box) td, .text-content th, .text-content td, input[type="email"], .submenu, button, .crumbs, select, .subnav > ol > li > a, .subnav .toggler, .text-content table.standard-table:not(.learn-box) td.header, #main-q, .home-hacks li, input[type="search"], .home-contribute li, body .topicpage-table .section, .zone-parent, .summary, input[type="text"], .output, .interactive, .text-content table.fullwidth-table th, .text-content table.fullwidth-table td, kbd
