kbd, a.button:focus, button.logout, button.transparent, .notification.info button, button:active, .news letter button { box-shadow: none }


/* Navigation buttons on some articles/tutorials */
span[style*="background: #eaeff2"] {
  background-color: var(--bg-input) !important;
  border: solid 0.5px var(--border-color);
  color: var(--fg-input) !important;
