/**** Tutorials - https://pytorch.org/tutorials *****/

/*** Main Page ***/

body {
  background-color: #282828;
  color: #ccc;
}

/*** Top Header ***/
.header-holder {
  background-color: #282828;
  border-bottom-color: #666;
}

/* Options */
.tutorials-header .main-menu ul li a {
  color: #ccc;
}

/*** Left Menu ***/
.pytorch-left-menu {
  background-color: #282828; /* rgb(52, 49, 49); */
}

/* Search */
.pytorch-left-menu-search input[type="text"] {
  background-color: #333;
  border-color: #333;
  color: #eee;
}

/* Links */
.pytorch-left-menu a:link, .pytorch-left-menu a:visited, .pytorch-left-menu a:hover, .pytorch-right-menu a:link, .pytorch-right-menu a:visited, .pytorch-right-menu a:hover {
  color: #ddd;
}

span.caption-text {
  color: #eee;
}

/*** Right Menu ***/
.pytorch-content-wrap {
  background-color: #282828;
}

/* Top bar */
.pytorch-page-level-bar {
  background-color: #282828;
  border-bottom-color: #666;
}

/* Text */
article.pytorch-article p, article.pytorch-article ul li, article.pytorch-article ol li, article.pytorch-article dl dt, article.pytorch-article dl dd, article.pytorch-article blockquote {
  color: #ccc;
}

/** Sub-section Headings **/
article.pytorch-article h2, article.pytorch-article h3, article.pytorch-article h4, article.pytorch-article h5, article.pytorch-article h6 {
  color: #ccc;
}

/*** Tutorial Page ***/

/** Right Side Text **/

article.pytorch-article h1 a, 
article.pytorch-article h2 a, 
article.pytorch-article h3 a, 
article.pytorch-article h4 a, 
article.pytorch-article h5 a, 
article.pytorch-article h6 a {
  color: #ccc;
}

/** Code **/
pre {
  background-color: #1c1c1c;
}

.pytorch-article div.sphx-glr-script-out .highlight pre {
  background-color: #222;
  color: #ccc;
}

/** 'Note:' **/
article.pytorch-article .note, article.pytorch-article .warning {
  background-color: #222;
}

article.pytorch-article .note p:nth-child(n+2), article.pytorch-article .warning p:nth-child(n+2) {
  color: #ddd;
}

/* inline Code */
article.pytorch-article .note .pre, article.pytorch-article .note pre, article.pytorch-article .warning .pre, article.pytorch-article .warning pre {
  background-color: #1c1c1c;
  outline-color: #1c1c1c;
}

article.pytorch-article .section :not(dt) > code {
  background-color: #1c1c1c;
  border-top-color: #1c1c1c;
  border-bottom-color: #1c1c1c;
}

span.pre {
  color: #F05732;
  background-color: #1c1c1c;
}

/** Syntax highlighting **/

pre {
  color: #ccc;
}

.highlight .nn {
  color: #ccc;
}

.highlight .o {
  color: #ccc;
}

/* Class */
.highlight .nc {
  color: #7aac18; /* #a6e22e; */
}

/* 'import' */
.highlight .kn {
  color: #007020;
}

/* 'print' function */
.highlight .k {
  color: #cd064d; /* #b80545; */
}

.highlight .kv {
  color: #cd064d;
}

/* 'in' */
.highlight .ow {
  color: #cd064d;
}

/* function */
.highlight .nf, .fm {
  color: #1aa2bb;
}

/* string, character */
.highlight .sd {
  color: #666;
}

.highlight .s1, .highlight .s2 {
  color: #080; /* #fe9; */
}

/* Self */
.highlight .bp {
  color: #FF80F4;
}

/* Types, 'len', 'isinstance' */
.highlight .nb {
  color: #00bfff;
}

/* True, False */
.highlight .kc {
  color: #ce7002;
}

/* Numerals */
.highlight .mi, .highlight .mf {
  color: #AE81FF;
}

/* Comments */
.highlight .c1 {
  color: #666;
}

/** Boxed below **/

.sphx-glr-thumbcontainer {
  background-color: #333;
}

article.pytorch-article .sphx-glr-thumbcontainer {
  border-color: #666;
}

article.pytorch-article .sphx-glr-thumbcontainer:hover {
  border-bottom-color: #666;
}

article.pytorch-article .sphx-glr-thumbcontainer::after {
  background-color: #666;
}

article.pytorch-article .sphx-glr-thumbcontainer .figure::before {
  background-color: #282828;
  opacity: 0.0;
}

article.pytorch-article .sphx-glr-thumbcontainer .caption-text a {
  color: #ccc;
}

/* Image */
.sphx-glr-thumbcontainer img {
  filter: invert(80%);
}

/** 'Download Python Source', 'Download Jupyter Notebook' **/

.pytorch-article div.sphx-glr-download a {
  background-color: #333;
}

.pytorch-article div.sphx-glr-download a:hover {
  background-color: #484848;
}

article.pytorch-article .function code, article.pytorch-article .attribute code, article.pytorch-article .class .attribute code, article.pytorch-article .class code {
  color: #ccc;
}

.pytorch-article div.sphx-glr-download a span.pre {
  color: #ccc;
}

/*** Search Results - pytorch.org/tutorials/search.html?q=? ***/

.rst-content .highlighted {
  background-color: #444;
}

/*** Docs - pytorch.org/docs e.g. pytorch.org/docs/stable/tensors.html ***/

/* Table */

article.pytorch-article table th {
  color: #aaa;
}

article.pytorch-article .wy-table-responsive table tbody td .pre {
  background-color: #333;
  outline-color: #333;
}

article.pytorch-article .wy-table-responsive table tbody .row-odd {
  background-color: #282828;
}

article.pytorch-article .wy-table-responsive table tbody td {
  color: #999;
}


/* Code */
article.pytorch-article .function dt, article.pytorch-article .class dt, article.pytorch-article .attribute dt, article.pytorch-article .class .attribute dt {
  background-color: #333;
}

article.pytorch-article .function dt > code, article.pytorch-article .class dt > code, article.pytorch-article .attribute dt > code, article.pytorch-article .class .attribute dt > code {
  color: #ccc;
  background-color: #333;
  border-top-color: #333;
  border-bottom-color: #333;
}

/* Text */
article.pytorch-article .function dd p, article.pytorch-article .class dd p, article.pytorch-article .attribute dd p, article.pytorch-article .class .attribute dd p {
  color: #ccc;
}

article.pytorch-article .function table tbody tr td.field-body, article.pytorch-article .class table tbody tr td.field-body, article.pytorch-article .attribute table tbody tr td.field-body {
  color: #ccc;
}

article.pytorch-article .function table tbody tr th.field-name, article.pytorch-article .class table tbody tr th.field-name, article.pytorch-article .attribute table tbody tr th.field-name {
  color: #ccc;
}

/* Text - Math expressions */
article.pytorch-article .math {
  color: #ccc;
}

/** 'Warning' **/
article.pytorch-article .warning .admonition-title {
  background-color: #532222; /* #e94f3b; */
}

article.pytorch-article .note p:nth-child(n+2), article.pytorch-article .warning p:nth-child(n+2) {
  color: #ccc;
}

/* small white box */
article.pytorch-article .admonition .last code {
  background-color: #1c1c1c;
  border-top-color: #1c1c1c;
  border-bottom-color: #1c1c1c;
  outline-color: #1c1c1c;
}

article.pytorch-article .note .first, article.pytorch-article .warning .first, article.pytorch-article .tip .first, article.pytorch-article .hint .first, article.pytorch-article .important .first, article.pytorch-article .caution .first, article.pytorch-article .danger .first, article.pytorch-article .attention .first, article.pytorch-article .error .first {
  color: #ccc;
}

/** 'Note' **/
article.pytorch-article .note .admonition-title {
  background-color: #183a44; /* #54c7ec; */
