  /**** Top Bar with Search box ****/

  .bg-white {
    background-color: #282828 !important;
  }

  .header {
    border-bottom-color: #333;
  }

  /* Search */
  .header .global-search {
    border-color: #ccc; /* for invert later */
    background-color: #eee; /* for invert later */
  }

  .header .header-search .icon {
    filter: invert(90%);
  }

  /* 'Trending Research' */
  h1 {
    color: #999;
  }

  /* 'Trending', 'Latest', 'Greatest', 'Subscribe' buttons */

  .list-button {
    background-color: #282828;
    border-color: #333;
    color: #999;
  }

  .list-button-active {
    background-color: #363636;
    border-color: #333;
    color: #999;
  }

  .list-button:hover {
    color: #ccc;
  }

  .list-button-active:hover {
    background-color: #363636;
    color: #999;
    border-color: #0069d9; /* from .btn-primary:hover */
  }

  /**** Paper List ****/

  /** Paper Row **/
  .row {
    border-color: #333;
  }

  .col {
    color: #aaa;
  }

  .col h5 {
    color: #999;
  }

  .col small {
    font-size: 90%;
    font-weight: normal;
  }

  /* Image */
  .col-mp .col-image img {
    filter: invert(86.5%);
  }

  /* 'Paper' button */
  .btn-primary:not(.list-button):not(.list-button-active):not(:hover) {
    background-color: #002c89; /* from #007bff */
    border-color: #002c89; /* from #007bff */
    color: #aaa;
  }

  .btn-primary:hover {
    color: #ccc;
  }

  /* 'Code' button */
  .btn-success {
    background-color: #124b1f; /* from #28a745 */
    border-color: #124b1f; /* from #28a745 */
    color: #aaa;
  }

  .btn-success:hover {
    color: #ccc;
  }

  /* Image below 'Paper' and 'Code' */

  /* for Tensorflow */
  img[src="static/frameworks/tf.png"] {
    filter: invert(86.5%);
  }


  /***** Footer *****/

  .footer {
    border-top-color: #333;
  }

